var Plotly = require('plotly.js/lib/core')
Plotly.register(require('plotly.js/lib/violin'))

var gd = document.createElement('div')
document.body.appendChild(gd)

var n = 100
var gen = v => Math.round(Math.random(v) * 10)
var x = new Array(n).fill(0).map(gen)
var y = new Array(n).fill(0).map(gen)

var c = '#984ea3'
var usmo = 0

Plotly.newPlot(gd, [{
  mode: 'markers',
  x: x,
  y: y,
  marker: {color: c},
  unselected: { marker: {opacity: usmo} }
}, {
  type: 'violin',
  name: 'x',
  x: x,
  points: 'all',
  pointpos: 1.5,
  box: {visible: true},
  yaxis: 'y2',
  marker: {color: c},
  unselected: { marker: {opacity: usmo} }
}, {
  type: 'violin',
  name: 'y',
  y: y,
  points: 'all',
  pointpos: 1.5,
  box: {visible: true},
  xaxis: 'x2',
  marker: {color: c},
  unselected: { marker: {opacity: usmo} }
}], {
  xaxis: {
    domain: [0, 0.48]
  },
  yaxis: {
    domain: [0, 0.48]
  },
  xaxis2: {
    domain: [0.52, 1]
  },
  yaxis2: {
    domain: [0.52, 1]
  },
  showlegend: false,
  dragmode: 'lasso',
  hovermode: 'closest',
  width: 700,
  height: 700
})

gd.on('plotly_selected', d => {
  if(d && d.points && d.points.length) {
    var sel = d.points.map(p => p.pointIndex)
    Plotly.restyle(gd, 'selectedpoints', [sel.slice(), sel.slice(), sel.slice()])
  } else {
    Plotly.restyle(gd, 'selectedpoints', null)
  }
})

{
  "name": "plotly.js-violins",
  "version": "1.0.1",
  "description": "plotly.js violins with persistent selections",
  "main": "index.js",
  "scripts": {
    "start": "budo index.js --open --live",
    "build": "browserify -t es2020 index.js | uglifyjs -c -m | indexhtmlify > index.html"
  },
  "keywords": [],
  "author": "Étienne Tétreault-Pinard <etienne.t.pinard@gmail.com>",
  "license": "MIT",
  "dependencies": {
    "plotly.js": "^1.37.1"
  },
  "devDependencies": {
    "browserify": "^16.2.2",
    "budo": "^11.2.2",
    "es2020": "^1.1.9",
    "indexhtmlify": "^1.3.1",
    "uglify-es": "^3.3.9"
  }
}
